INSERT INTO music_letter (`id`,`name`,`songwriter`,`music_letter`,`video_link`,`users_id`,`genre_id`,`artist_id`) VALUES (27,'War Pigs','Bill Ward \ Geezer Butler \ Ozzy Osbourne \ Tony Iommi. ','Generals gathered in their masses\nJust like witches at black masses\nEvil minds that plot destruction\nSorcerers of death\'s construction\n\nIn the fields, the bodies burning\nAs the war machine keeps turning\nDeath and hatred to mankind\nPoisoning their brainwashed minds\nOh, Lord, yeah!\n\nPoliticians hide themselves away\nThey only started the war\nWhy should they go out to fight?\nThey leave that all to the poor, yeah!\n\nTime will tell on their power minds\nMaking war just for fun\nTreating people just like pawns in chess\nWait till their judgment day comes, yeah!\n\nNow, in darkness, world stops turning\nAshes where their bodies burning\nNo more war pigs have the power\nHand of God has struck the hour\n\nDay of Judgment, God is calling\nOn their knees, the war pigs crawling\nBegging mercies for their sins\nSatan, laughing, spreads his wings\nOh, Lord, yeah!','https://www.youtube.com/watch?v=f-e8-DUqt0I',9,4,7);
INSERT INTO music_letter (`id`,`name`,`songwriter`,`music_letter`,`video_link`,`users_id`,`genre_id`,`artist_id`) VALUES (28,'Iron Man','Bill Ward \ Geezer Butler \ Ozzy Osbourne \ Tony Iommi.','Has he lost his mind?\nCan he see or is he blind?\nCan he walk at all\nOr if he moves, will he fall?\n\nIs he alive or dead?\nHas he thoughts within his head?\nWe\'ll just pass him there\nWhy should we even care?\n\nHe was turned to steel\nIn the great magnetic field\nWhen he travelled time\nFor the future of mankind\n\nNobody wants him\nHe just stares at the world\nPlanning his vengeance\nThat he will soon unfold\n\nNow the time is here\nFor iron man to spread fear\nVengeance from the grave\nKills the people he once saved\n\nNobody wants him\nThey just turn their heads\nNobody helps him\nNow he has his revenge\n\nHeavy boots of lead\nFills his victims full of dread\nRunning as fast as they can\nIron man lives again!','https://www.youtube.com/watch?v=8aQRq9hhekA&t=4s',9,4,7);
INSERT INTO music_letter (`id`,`name`,`songwriter`,`music_letter`,`video_link`,`users_id`,`genre_id`,`artist_id`) VALUES (29,'Changes','Bill Ward \ Geezer Butler \ Ozzy Osbourne \ Tony Iommi.','I feel unhappy\nI feel so sad\nI\'ve lost the best friend\nThat I ever had\nShe was my woman\nI love her so\nBut it\'s too late now\nI\'ve let her go\n\nI\'m going through changes\nI\'m going through changes\n\nWe shared the years\nWe shared each day\nIn love together\nWe found a way\nBut soon the world\nHad its evil way\nMy heart was blinded\nLove went astray\n\nI\'m going through changes\nI\'m going through changes\n\nIt took so long\nTo realize\nAnd I can still hear\nHer last goodbyes\nNow all my days\nAre filled with tears\nWish I could go back\nAnd change these years\n\nI\'m going through changes\nI\'m going through changes','https://www.youtube.com/watch?v=3zCVlEupuFI&t=35s',9,4,7);
INSERT INTO music_letter (`id`,`name`,`songwriter`,`music_letter`,`video_link`,`users_id`,`genre_id`,`artist_id`) VALUES (30,'Solitude','Bill Ward \ Geezer Butler \ Ozzy Osbourne \ Tony Iommi.','My name it means nothing, my fortune is less\nMy future is shrouded in dark wilderness\nSunshine is far away, clouds linger on\nEverything I posessed, now they are gone\n\nOh, where can I go to and what can I do?\nNothing can please me only thoughts are of you\nYou just laughed when I begged you to stay\nI\'ve not stopped crying since you went away\n\nThe world is a lonely place, you\'re on your own\nGuess I will go home, sit down and moan\nCrying and thinking is all that I do\nMemories I have remind me of you','https://www.youtube.com/watch?v=vmV8niW5GXs&t=31s',9,4,7);
